// src/Dashboard.js - Modified version with Lecturer functionalities
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard({ token, role }) {
  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [ratings, setRatings] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [classes, setClasses] = useState([]); // For classes
  const [monitoringData, setMonitoringData] = useState([]); // For monitoring
  const [view, setView] = useState('reports'); // To switch between Views: reports, classes, monitoring, ratings

  useEffect(() => {
    fetchData();
    if(role === 'lecturer') {
      fetchLecturerData();
    }
  }, [view]);

  const fetchData = async (url = '/api/reports/view') => {
    try {
      const res = await axios.get(`http://localhost:5000${url}`, { headers: { Authorization: token } });
      setReports(res.data);
    } catch (err) {
      alert('Failed to load reports');
    }
  };

  const fetchLecturerData = async () => {
    if(role !== 'lecturer') return;
    try {
      // Fetch classes
      const classesRes = await axios.get('http://localhost:5000/api/lecturer/classes', { headers: { Authorization: token } });
      setClasses(classesRes.data);

      // Fetch monitoring data
      const monitoringRes = await axios.get('http://localhost:5000/api/lecturer/monitoring', { headers: { Authorization: token } });
      setMonitoringData(monitoringRes.data);
      
      // Fetch reports if needed
      fetchData();
    } catch (err) {
      alert('Failed to load lecturer data');
    }
  };

  const handleSearch = () => {
    if (searchQuery.trim()) {
      fetchData(`/api/reports/search?query=${encodeURIComponent(searchQuery)}`);
    } else {
      fetchData();
    }
  };

  const handleFeedback = async (id) => {
    if (role !== 'prl') return;
    try {
      await axios.put(`http://localhost:5000/api/reports/feedback/${id}`, 
        { feedback: feedback[id] }, 
        { headers: { Authorization: token } }
      );
      alert('Feedback added');
      fetchData();
    } catch (err) {
      alert('Failed to add feedback');
    }
  };

  const handleRate = async (id) => {
    if (!ratings[id] || ratings[id] < 1 || ratings[id] > 5) {
      alert('Please enter a rating between 1 and 5');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/rate', 
        { report_id: id, rating: parseInt(ratings[id]) }, 
        { headers: { Authorization: token } }
      );
      alert('Rated successfully');
      fetchData();
    } catch (err) {
      alert('Failed to rate');
    }
  };

  const downloadExcel = async (id) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/reports/download/${id}`, 
        { 
          headers: { Authorization: token }, 
          responseType: 'blob' 
        }
      );
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report_${id}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      alert('Download failed');
    }
  };

  // Chart data for reports
  const attendanceData = {
    labels: reports.map(r => r.course_name || 'Unknown'),
    datasets: [
      {
        label: 'Actual Students',
        data: reports.map(r => r.actual_students || 0),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
      {
        label: 'Registered Students',
        data: reports.map(r => r.total_registered_students || 0),
        backgroundColor: 'rgba(153, 102, 255, 0.6)',
      },
    ],
  };

  const ratingData = {
    labels: reports.map(r => r.course_name || 'Unknown'),
    datasets: [
      {
        label: 'Ratings',
        data: reports.map(r => r.rating || 0),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: { 
      legend: { position: 'top' }, 
      title: { display: true, text: 'Attendance Overview' } 
    },
  };

  return (
    <div className="mt-4">
      <h2>Dashboard - {role.toUpperCase()}</h2>

      {/* Navigation Tabs for different functionalities */}
      {role === 'lecturer' && (
        <div className="mb-3">
          <button className={`btn btn-${view==='reports'?'primary':'secondary'} mr-2`} onClick={() => setView('reports')}>Reports</button>
          <button className={`btn btn-${view==='classes'?'primary':'secondary'} mr-2`} onClick={() => setView('classes')}>Classes</button>
          <button className={`btn btn-${view==='monitoring'?'primary':'secondary'} mr-2`} onClick={() => setView('monitoring')}>Monitoring</button>
          <button className={`btn btn-${view==='ratings'?'primary':'secondary'}`} onClick={() => setView('ratings')}>Ratings</button>
        </div>
      )}

      {/* View: Reports */}
      {view === 'reports' && (
        <>
          {/* Search Section */}
          <div className="mb-3">
            <input 
              type="text" 
              placeholder="Search by course name or week..." 
              value={searchQuery} 
              onChange={(e) => setSearchQuery(e.target.value)}
              className="form-control d-inline-block w-25 mr-2"
            />
            <button onClick={handleSearch} className="btn btn-info mr-2">Search</button>
            <button onClick={() => { setSearchQuery(''); fetchData(); }} className="btn btn-secondary">
              Clear
            </button>
          </div>

          {/* Charts Section */}
          {reports.length > 0 && (
            <>
              <div style={{ height: '400px', margin: '20px 0' }}>
                <Bar data={attendanceData} options={chartOptions} />
              </div>
              <div style={{ height: '400px', margin: '20px 0' }}>
                <Bar 
                  data={ratingData} 
                  options={{ 
                    ...chartOptions, 
                    plugins: { 
                      ...chartOptions.plugins,
                      title: { ...chartOptions.plugins.title, text: 'Rating Overview' } 
                    } 
                  }} 
                />
              </div>
            </>
          )}

          {/* Reports Table */}
          <table className="table table-striped">
            <thead>
              <tr>
                <th>ID</th>
                <th>Course</th>
                <th>Week</th>
                <th>Lecturer</th>
                <th>Feedback</th>
                <th>Rating</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {reports.length === 0 ? (
                <tr>
                  <td colSpan="7" className="text-center">No reports found</td>
                </tr>
              ) : (
                reports.map(report => (
                  <tr key={report.id}>
                    <td>{report.id}</td>
                    <td>{report.course_name}</td>
                    <td>{report.week_of_reporting}</td>
                    <td>{report.lecturer_name}</td>
                    <td>{report.feedback || 'None'}</td>
                    <td>{report.rating ? `${report.rating}/5` : 'Not rated'}</td>
                    <td>
                      <div className="btn-group">
                        {role === 'prl' && (
                          <>
                            <input 
                              type="text" 
                              placeholder="Add feedback..."
                              onChange={(e) => setFeedback({ ...feedback, [report.id]: e.target.value })}
                              className="form-control d-inline-block w-50 mr-2"
                            />
                            <button 
                              onClick={() => handleFeedback(report.id)} 
                              className="btn btn-success btn-sm"
                            >
                              Add Feedback
                            </button>
                          </>
                        )}
                        <input 
                          type="number" 
                          min="1" 
                          max="5" 
                          placeholder="1-5"
                          onChange={(e) => setRatings({ ...ratings, [report.id]: e.target.value })}
                          className="form-control d-inline-block w-25 mr-2"
                        />
                        <button 
                          onClick={() => handleRate(report.id)} 
                          className="btn btn-warning btn-sm mr-2"
                        >
                          Rate
                        </button>
                        <button 
                          onClick={() => downloadExcel(report.id)} 
                          className="btn btn-primary btn-sm"
                        >
                          Excel
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </>
      )}

      {/* View: Classes */}
      {view === 'classes' && (
        <div>
          <h3>Your Classes</h3>
          {classes.length === 0 ? (
            <p>No classes assigned.</p>
          ) : (
            <ul className="list-group">
              {classes.map((cls) => (
                <li key={cls.id} className="list-group-item">
                  {cls.course_name} - {cls.lecture_time}
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* View: Monitoring */}
      {view === 'monitoring' && (
        <div>
          <h3>Monitoring Data</h3>
          {monitoringData.length === 0 ? (
            <p>No monitoring data available.</p>
          ) : (
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Course</th>
                  <th>Attendance</th>
                  <th>Week</th>
                </tr>
              </thead>
              <tbody>
                {monitoringData.map((data, index) => (
                  <tr key={index}>
                    <td>{data.course_name}</td>
                    <td>{data.attendance_percentage}%</td>
                    <td>{data.week}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}

      {/* View: Ratings (for lecturer to see ratings) */}
      {view === 'ratings' && (
        <div>
          <h3>Ratings Overview</h3>
          {/* Could add charts or list ratings here */}
          {reports.length > 0 ? (
            <Bar 
              data={ratingData} 
              options={{ 
                ...chartOptions, 
                plugins: { 
                  ...chartOptions.plugins,
                  title: { ...chartOptions.plugins.title, text: 'Ratings Overview' } 
                } 
              }} 
            />
          ) : (
            <p>No ratings data available.</p>
          )}
        </div>
      )}

      {/* Submit New Report Button for lecturers */}
      {role === 'lecturer' && (
        <a href="/report" className="btn btn-primary mt-3">Submit New Report</a>
      )}
    </div>
  );
}

export default Dashboard;
