// src/Dashboard.js - Updated with charts
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard({ token, role }) {
  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [ratings, setRatings] = useState({});
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async (url = '/api/reports/view') => {
  try {
    // Fixed header key to match backend expectation
    const res = await axios.get(`http://localhost:5000${url}`, { headers: { 'authorization': token } });
    setReports(res.data);
  } catch (err) {
    alert('Failed to load');
  }
};

  const handleSearch = () => {
    fetchData(`/api/reports/search?query=${searchQuery}`);
  };

  const handleFeedback = async (id) => {
    if (role !== 'prl') return;
    try {
      await axios.put(`http://localhost:5000/api/reports/feedback/${id}`, { feedback: feedback[id] }, { headers: { Authorization: token } });
      alert('Feedback added');
      fetchData();
    } catch (err) {
      alert('Failed');
    }
  };

  const handleRate = async (id) => {
    try {
      await axios.post('http://localhost:5000/api/rate', { report_id: id, rating: ratings[id] }, { headers: { Authorization: token } });
      alert('Rated');
      fetchData();
    } catch (err) {
      alert('Failed');
    }
  };

  const downloadExcel = async (id) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/reports/download/${id}`, { headers: { Authorization: token }, responseType: 'blob' });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'report.xlsx');
      document.body.appendChild(link);
      link.click();
    } catch (err) {
      alert('Download failed');
    }
  };

  // Chart Data
  const attendanceData = {
    labels: reports.map(r => r.course_name || 'Unknown'),
    datasets: [
      {
        label: 'Actual Students',
        data: reports.map(r => r.actual_students || 0),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
      {
        label: 'Registered Students',
        data: reports.map(r => r.total_registered_students || 0),
        backgroundColor: 'rgba(153, 102, 255, 0.6)',
      },
    ],
  };

  const ratingData = {
    labels: reports.map(r => r.course_name || 'Unknown'),
    datasets: [
      {
        label: 'Average Rating',
        data: reports.map(r => {
          const reportRatings = ratings.filter(rat => rat.report_id === r.id);
          return reportRatings.length ? reportRatings.reduce((a, b) => a + b.rating, 0) / reportRatings.length : 0;
        }),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: { legend: { position: 'top' }, title: { display: true, text: 'Attendance Overview' } },
  };

  return (
    <div className="mt-4">
      <h2>Dashboard</h2>
      <input type="text" placeholder="Search" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
      <button onClick={handleSearch} className="btn btn-info">Search</button>
      <div style={{ height: '400px', margin: '20px 0' }}>
        <Bar data={attendanceData} options={chartOptions} />
      </div>
      <div style={{ height: '400px', margin: '20px 0' }}>
        <Bar data={ratingData} options={{ ...chartOptions, plugins: { title: { text: 'Rating Overview' } } }} />
      </div>
      <table className="table">
        <thead><tr><th>ID</th><th>Course</th><th>Week</th><th>Feedback</th><th>Rating</th><th>Actions</th></tr></thead>
        <tbody>
          {reports.map(report => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.course_name}</td>
              <td>{report.week_of_reporting}</td>
              <td>{report.feedback || 'None'}</td>
              <td>{report.rating || 'None'}</td>
              <td>
                {role === 'prl' && (
                  <>
                    <input type="text" onChange={(e) => setFeedback({ ...feedback, [report.id]: e.target.value })} />
                    <button onClick={() => handleFeedback(report.id)} className="btn btn-success">Feedback</button>
                  </>
                )}
                <input type="number" min="1" max="5" onChange={(e) => setRatings({ ...ratings, [report.id]: e.target.value })} />
                <button onClick={() => handleRate(report.id)} className="btn btn-warning">Rate</button>
                <button onClick={() => downloadExcel(report.id)} className="btn btn-primary">Excel</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {role === 'lecturer' && <a href="/report" className="btn btn-primary">Submit Report</a>}
    </div>
  );
}

export default Dashboard;
