// src/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard({ token, role }) {
  const [activeTab, setActiveTab] = useState("reports");

  // Data states
  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [ratings, setRatings] = useState({});
  const [searchQuery, setSearchQuery] = useState("");

  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState("");
  const [lecturerAssignments, setLecturerAssignments] = useState({});

  const [classes, setClasses] = useState([]);
  const [newClass, setNewClass] = useState("");

  const [lectures, setLectures] = useState([]);
  const [newLecture, setNewLecture] = useState("");

  const [monitoringData, setMonitoringData] = useState([]);

  const [newReport, setNewReport] = useState({
    faculty_name: "",
    class_name: "",
    week_of_reporting: "",
    date_of_lecture: "",
    course_name: "",
    course_code: "",
    lecturer_name: "",
    actual_students: "",
    total_registered_students: "",
    venue: "",
    scheduled_time: "",
    topic_taught: "",
    learning_outcomes: "",
    recommendations: "",
  });

  // Fetch functions
  const fetchReports = async (query = "") => {
    try {
      const url = query
        ? `/api/reports/search?query=${encodeURIComponent(query)}`
        : "/api/reports/view";
      const res = await axios.get(`http://localhost:5000${url}`, {
        headers: { Authorization: token },
      });
      setReports(res.data);
    } catch {
      alert("Failed to load reports");
    }
  };

  const fetchCourses = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/courses", {
        headers: { Authorization: token },
      });
      setCourses(res.data);
    } catch {
      alert("Failed to load courses");
    }
  };

  const fetchClasses = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/classes", {
        headers: { Authorization: token },
      });
      setClasses(res.data);
    } catch {
      alert("Failed to load classes");
    }
  };

  const fetchLectures = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/lectures", {
        headers: { Authorization: token },
      });
      setLectures(res.data);
    } catch {
      alert("Failed to load lectures");
    }
  };

  const fetchMonitoring = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/monitoring", {
        headers: { Authorization: token },
      });
      setMonitoringData(res.data);
    } catch {
      alert("Failed to load monitoring data");
    }
  };

  // Handlers
  const handleAddCourse = async () => {
    if (!newCourse.trim()) return;
    try {
      await axios.post(
        "http://localhost:5000/api/courses",
        { name: newCourse.trim() },
        { headers: { Authorization: token } }
      );
      setNewCourse("");
      fetchCourses();
    } catch {
      alert("Failed to add course");
    }
  };

  const handleAssignLecturer = async (courseId) => {
    const lecturerId = lecturerAssignments[courseId]?.trim();
    if (!lecturerId) return;
    try {
      await axios.put(
        `http://localhost:5000/api/courses/${courseId}/assign`,
        { lecturerId },
        { headers: { Authorization: token } }
      );
      alert("Lecturer assigned");
      fetchCourses();
    } catch {
      alert("Failed to assign lecturer");
    }
  };

  const handleAddClass = async () => {
    if (!newClass.trim()) return;
    try {
      await axios.post(
        "http://localhost:5000/api/classes",
        { name: newClass.trim() },
        { headers: { Authorization: token } }
      );
      setNewClass("");
      fetchClasses();
    } catch {
      alert("Failed to add class");
    }
  };

  const handleAddLecture = async () => {
    if (!newLecture.trim()) return;
    try {
      await axios.post(
        "http://localhost:5000/api/lectures",
        { title: newLecture.trim() },
        { headers: { Authorization: token } }
      );
      setNewLecture("");
      fetchLectures();
    } catch {
      alert("Failed to add lecture");
    }
  };

  const handleFeedback = async (id) => {
    if (role !== "prl") return;
    try {
      await axios.put(
        `http://localhost:5000/api/reports/feedback/${id}`,
        { feedback: feedback[id]?.trim() || "" },
        { headers: { Authorization: token } }
      );
      alert("Feedback added");
      fetchReports();
    } catch {
      alert("Failed to add feedback");
    }
  };

  const handleRate = async (id) => {
    const rateValue = parseInt(ratings[id]);
    if (!rateValue || rateValue < 1 || rateValue > 5) {
      alert("Enter a rating 1-5");
      return;
    }
    try {
      await axios.post(
        "http://localhost:5000/api/rate",
        { report_id: id, rating: rateValue },
        { headers: { Authorization: token } }
      );
      alert("Rated successfully");
      fetchReports();
    } catch {
      alert("Failed to rate");
    }
  };

  const downloadExcel = async (id) => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/reports/download/${id}`,
        { headers: { Authorization: token }, responseType: "blob" }
      );
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `report_${id}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch {
      alert("Download failed");
    }
  };

  const handleAddReport = async () => {
    try {
      await axios.post(
        "http://localhost:5000/api/reports",
        newReport,
        { headers: { Authorization: token } }
      );
      alert("Report added");
      setNewReport({
        faculty_name: "",
        class_name: "",
        week_of_reporting: "",
        date_of_lecture: "",
        course_name: "",
        course_code: "",
        lecturer_name: "",
        actual_students: "",
        total_registered_students: "",
        venue: "",
        scheduled_time: "",
        topic_taught: "",
        learning_outcomes: "",
        recommendations: "",
      });
      fetchReports();
    } catch {
      alert("Failed to add report");
    }
  };

  // Role-based tabs
  const roleTabs = {
    student: ["monitoring", "reports"],
    lecturer: ["classes", "reports", "monitoring"],
    prl: ["courses", "reports", "monitoring", "classes"],
    pl: ["courses", "reports", "monitoring", "classes", "lectures"],
  };

  useEffect(() => {
    switch (activeTab) {
      case "reports":
        fetchReports();
        break;
      case "courses":
        fetchCourses();
        break;
      case "classes":
        fetchClasses();
        break;
      case "lectures":
        fetchLectures();
        break;
      case "monitoring":
        fetchMonitoring();
        break;
      default:
        break;
    }
  }, [activeTab]);

  // Chart Data
  const attendanceData = {
    labels: reports.map((r) => r.course_name || "Unknown"),
    datasets: [
      {
        label: "Actual Students",
        data: reports.map((r) => r.actual_students || 0),
        backgroundColor: "rgba(75,192,192,0.6)",
      },
      {
        label: "Registered Students",
        data: reports.map((r) => r.total_registered_students || 0),
        backgroundColor: "rgba(153,102,255,0.6)",
      },
    ],
  };

  const ratingData = {
    labels: reports.map((r) => r.course_name || "Unknown"),
    datasets: [
      {
        label: "Ratings",
        data: reports.map((r) => r.rating || 0),
        backgroundColor: "rgba(255,99,132,0.6)",
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "Attendance Overview" },
    },
  };

  return (
    <div className="mt-4">
      <h2>Dashboard - {role.toUpperCase()}</h2>

      {/* Tabs */}
      <div className="mb-4">
        {roleTabs[role]?.map((tab) => (
          <button
            key={tab}
            className={`btn btn-${activeTab === tab ? "primary" : "secondary"} mr-2`}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {/* Dynamic Tab Content */}
      {activeTab === "reports" && (
        <ReportsTab
          role={role}
          reports={reports}
          newReport={newReport}
          setNewReport={setNewReport}
          feedback={feedback}
          setFeedback={setFeedback}
          ratings={ratings}
          setRatings={setRatings}
          searchQuery={searchQuery}
          setSearchQuery={setSearchQuery}
          fetchReports={fetchReports}
          handleAddReport={handleAddReport}
          handleFeedback={handleFeedback}
          handleRate={handleRate}
          downloadExcel={downloadExcel}
        />
      )}

      {activeTab === "courses" && (
        <CoursesTab
          courses={courses}
          newCourse={newCourse}
          setNewCourse={setNewCourse}
          lecturerAssignments={lecturerAssignments}
          setLecturerAssignments={setLecturerAssignments}
          handleAddCourse={handleAddCourse}
          handleAssignLecturer={handleAssignLecturer}
        />
      )}

      {activeTab === "classes" && (
        <ClassesTab
          classes={classes}
          newClass={newClass}
          setNewClass={setNewClass}
          handleAddClass={handleAddClass}
        />
      )}

      {activeTab === "lectures" && role === "pl" && (
        <LecturesTab
          lectures={lectures}
          newLecture={newLecture}
          setNewLecture={setNewLecture}
          handleAddLecture={handleAddLecture}
        />
      )}

      {activeTab === "monitoring" && (
        <MonitoringTab monitoringData={monitoringData} />
      )}

      {/* Charts */}
      {reports.length > 0 && (
        <>
          <div style={{ height: "400px", margin: "20px 0" }}>
            <Bar data={attendanceData} options={chartOptions} />
          </div>
          <div style={{ height: "400px", margin: "20px 0" }}>
            <Bar
              data={ratingData}
              options={{
                ...chartOptions,
                plugins: {
                  ...chartOptions.plugins,
                  title: { ...chartOptions.plugins.title, text: "Rating Overview" },
                },
              }}
            />
          </div>
        </>
      )}
    </div>
  );
}

export default Dashboard;
