// src/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard({ token, role }) {
  const [activeTab, setActiveTab] = useState("reports");

  // Data states
  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [ratings, setRatings] = useState({});
  const [searchQuery, setSearchQuery] = useState("");

  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState("");
  const [lecturerAssignments, setLecturerAssignments] = useState({});

  const [classes, setClasses] = useState([]);
  const [newClass, setNewClass] = useState("");

  const [lectures, setLectures] = useState([]);
  const [newLecture, setNewLecture] = useState("");

  const [monitoringData, setMonitoringData] = useState([]);

  const [newReport, setNewReport] = useState({
    faculty_name: "",
    class_name: "",
    week_of_reporting: "",
    date_of_lecture: "",
    course_name: "",
    course_code: "",
    lecturer_name: "",
    actual_students: "",
    total_registered_students: "",
    venue: "",
    scheduled_time: "",
    topic_taught: "",
    learning_outcomes: "",
    recommendations: "",
  });

  // Fetch reports
  const fetchReports = async (query = "") => {
    try {
      const url = query
        ? `/api/reports/search?query=${encodeURIComponent(query)}`
        : "/api/reports/view";
      const res = await axios.get(`http://localhost:5000${url}`, {
        headers: { Authorization: token },
      });
      setReports(res.data);
    } catch (error) {
      console.error("Error fetching reports:", error);
      alert("Failed to load reports");
    }
  };

  // Fetch courses
  const fetchCourses = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/courses", {
        headers: { Authorization: token },
      });

      if (role === "lecturer") {
        const userId = localStorage.getItem("userId");
        const assignedCourses = res.data.filter(
          (course) => course.lecturer_id === parseInt(userId)
        );
        setCourses(assignedCourses);
      } else {
        setCourses(res.data);
      }
    } catch (error) {
      console.error("Error fetching courses:", error);
      alert("Failed to load courses");
    }
  };

  // Fetch classes
  const fetchClasses = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/classes", {
        headers: { Authorization: token },
      });
      setClasses(res.data);
    } catch (error) {
      console.error("Error fetching classes:", error);
      alert("Failed to load classes");
    }
  };

  // Fetch lectures
  const fetchLectures = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/lectures", {
        headers: { Authorization: token },
      });
      setLectures(res.data);
    } catch (error) {
      console.error("Error fetching lectures:", error);
      alert("Failed to load lectures");
    }
  };

  // Fetch monitoring data
  const fetchMonitoring = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/monitoring", {
        headers: { Authorization: token },
      });
      setMonitoringData(res.data);
    } catch (error) {
      console.error("Error fetching monitoring data:", error);
      alert("Failed to load monitoring data");
    }
  };

  // Add new course
  const handleAddCourse = async () => {
    if (!newCourse) return;
    try {
      await axios.post(
        "http://localhost:5000/api/courses",
        { name: newCourse },
        { headers: { Authorization: token } }
      );
      setNewCourse("");
      fetchCourses();
    } catch (error) {
      console.error("Error adding course:", error);
      alert("Failed to add course");
    }
  };

  // Assign lecturer to a course
  const handleAssignLecturer = async (courseId) => {
    const lecturerId = lecturerAssignments[courseId];
    if (!lecturerId) return;
    try {
      await axios.put(
        `http://localhost:5000/api/courses/${courseId}/assign`,
        { lecturerId },
        { headers: { Authorization: token } }
      );
      alert("Lecturer assigned successfully");
      fetchCourses();
    } catch (error) {
      console.error("Error assigning lecturer:", error);
      alert("Failed to assign lecturer");
    }
  };

  // Add new class
  const handleAddClass = async () => {
    if (!newClass) return;
    try {
      await axios.post(
        "http://localhost:5000/api/classes",
        { name: newClass },
        { headers: { Authorization: token } }
      );
      setNewClass("");
      fetchClasses();
    } catch (error) {
      console.error("Error adding class:", error);
      alert("Failed to add class");
    }
  };

  // Add new lecture
  const handleAddLecture = async () => {
    if (!newLecture) return;
    try {
      await axios.post(
        "http://localhost:5000/api/lectures",
        { title: newLecture },
        { headers: { Authorization: token } }
      );
      setNewLecture("");
      fetchLectures();
    } catch (error) {
      console.error("Error adding lecture:", error);
      alert("Failed to add lecture");
    }
  };

  // Feedback submission (PRL only)
  const handleFeedback = async (reportId) => {
    if (role !== "prl") return;
    try {
      await axios.put(
        `http://localhost:5000/api/reports/feedback/${reportId}`,
        { feedback: feedback[reportId] },
        { headers: { Authorization: token } }
      );
      alert("Feedback submitted successfully");
      fetchReports();
    } catch (error) {
      console.error("Error submitting feedback:", error);
      alert("Failed to submit feedback");
    }
  };

  // Rate a report
  const handleRate = async (reportId) => {
    if (!ratings[reportId] || ratings[reportId] < 1 || ratings[reportId] > 5) {
      alert("Please enter a rating between 1 and 5");
      return;
    }
    try {
      await axios.post(
        "http://localhost:5000/api/rate",
        { report_id: reportId, rating: parseInt(ratings[reportId]) },
        { headers: { Authorization: token } }
      );
      alert("Rating submitted successfully");
      fetchReports();
    } catch (error) {
      console.error("Error rating report:", error);
      alert("Failed to submit rating");
    }
  };

  // Download report as Excel
  const downloadExcel = async (reportId) => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/reports/download/${reportId}`,
        { headers: { Authorization: token }, responseType: "blob" }
      );
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `report_${reportId}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error("Error downloading report:", error);
      alert("Failed to download report");
    }
  };

  // Add new report
  const handleAddReport = async () => {
    try {
      await axios.post(
        "http://localhost:5000/api/reports",
        newReport,
        { headers: { Authorization: token } }
      );
      alert("Report added successfully");
      setNewReport({
        faculty_name: "",
        class_name: "",
        week_of_reporting: "",
        date_of_lecture: "",
        course_name: "",
        course_code: "",
        lecturer_name: "",
        actual_students: "",
        total_registered_students: "",
        venue: "",
        scheduled_time: "",
        topic_taught: "",
        learning_outcomes: "",
        recommendations: "",
      });
      fetchReports();
    } catch (error) {
      console.error("Error adding report:", error);
      alert("Failed to add report");
    }
  };

  // Role-based tabs
  const roleTabs = {
    student: ["monitoring", "ratings"],
    lecturer: ["classes", "reports", "monitoring"],
    prl: ["courses", "reports", "monitoring", "classes"],
    pl: ["courses", "reports", "monitoring", "classes", "lectures"],
  };

  // Fetch data when tab changes
  useEffect(() => {
    if (activeTab === "reports") fetchReports();
    if (activeTab === "courses") fetchCourses();
    if (activeTab === "classes") fetchClasses();
    if (activeTab === "lectures") fetchLectures();
    if (activeTab === "monitoring") fetchMonitoring();
  }, [activeTab]);

  // Chart data
  const attendanceData = {
    labels: reports.map((r) => r.course_name || "Unknown"),
    datasets: [
      {
        label: "Actual Students",
        data: reports.map((r) => r.actual_students || 0),
        backgroundColor: "rgba(54, 162, 235, 0.7)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 1,
      },
      {
        label: "Registered Students",
        data: reports.map((r) => r.total_registered_students || 0),
        backgroundColor: "rgba(153, 102, 255, 0.7)",
        borderColor: "rgba(153, 102, 255, 1)",
        borderWidth: 1,
      },
    ],
  };

  const ratingData = {
    labels: reports.map((r) => r.course_name || "Unknown"),
    datasets: [
      {
        label: "Ratings",
        data: reports.map((r) => r.rating || 0),
        backgroundColor: "rgba(255, 159, 64, 0.7)",
        borderColor: "rgba(255, 159, 64, 1)",
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "Attendance Overview" },
    },
  };

  return (
    <div className="mt-4">
      <h2>Dashboard - {role.toUpperCase()}</h2>

      {/* Tabs */}
      <div className="mb-4">
        {roleTabs[role]?.map((tab) => (
          <button
            key={tab}
            className={`btn btn-${activeTab === tab ? "primary" : "secondary"} mr-2`}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {/* Monitoring Tab */}
      {activeTab === "monitoring" && (
        <div>
          <h5>Monitoring</h5>
          {monitoringData.length === 0 ? (
            <p>No monitoring data available</p>
          ) : (
            <table className="table table-striped">
              <thead>
                <tr>
                  {role === "student" && (
                    <>
                      <th>Course</th>
                      <th>Week</th>
                      <th>Attended</th>
                      <th>Registered</th>
                      <th>Rating</th>
                    </>
                  )}
                  {role === "lecturer" && (
                    <>
                      <th>Course</th>
                      <th>Week</th>
                      <th>Attended</th>
                      <th>Feedback</th>
                    </>
                  )}
                  {role === "prl" && (
                    <>
                      <th>Course</th>
                      <th>Week</th>
                      <th>Lecturer</th>
                      <th>Attended</th>
                      <th>Feedback</th>
                    </>
                  )}
                  {role === "pl" && (
                    <>
                      <th>Course</th>
                      <th>Week</th>
                      <th>Lecturer</th>
                      <th>Attended</th>
                      <th>Registered</th>
                      <th>Rating</th>
                    </>
                  )}
                </tr>
              </thead>
              <tbody>
                {monitoringData.length === 0 ? (
                  <tr>
                    <td
                      colSpan={
                        role === "pl"
                          ? 6
                          : role === "prl"
                          ? 5
                          : role === "lecturer"
                          ? 4
                          : 5
                      }
                      className="text-center"
                    >
                      No monitoring data available
                    </td>
                  </tr>
                ) : (
                  monitoringData.map((item) => (
                    <tr key={item.id}>
                      {role === "student" && (
                        <>
                          <td>{item.course_name}</td>
                          <td>{item.week_of_reporting}</td>
                          <td>{item.actual_students}</td>
                          <td>{item.total_registered_students}</td>
                          <td>{item.rating || "N/A"}</td>
                        </>
                      )}
                      {role === "lecturer" && (
                        <>
                          <td>{item.course_name}</td>
                          <td>{item.week_of_reporting}</td>
                          <td>{item.actual_students}</td>
                          <td>{item.feedback || "None"}</td>
                        </>
                      )}
                      {role === "prl" && (
                        <>
                          <td>{item.course_name}</td>
                          <td>{item.week_of_reporting}</td>
                          <td>{item.lecturer_name}</td>
                          <td>
                            {item.actual_students}/{item.total_registered_students}
                          </td>
                          <td>{item.feedback || "Pending"}</td>
                        </>
                      )}
                      {role === "pl" && (
                        <>
                          <td>{item.course_name}</td>
                          <td>{item.week_of_reporting}</td>
                          <td>{item.lecturer_name}</td>
                          <td>{item.actual_students}</td>
                          <td>{item.total_registered_students}</td>
                          <td>{item.rating || "N/A"}</td>
                        </>
                      )}
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          )}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
