import React, { useState } from 'react';
import axios from 'axios';

function Auth({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student');
  const [faculty_name, setFacultyName] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await axios.post('http://localhost:5000/api/auth/login', { username, password });
        onLogin(res.data.token, res.data.role);
        window.location.href = '/dashboard';
      } else {
        await axios.post('http://localhost:5000/api/auth/register', { username, password, role, faculty_name });
        alert('Registered! Switch to login.');
        setIsLogin(true);
      }
    } catch (err) {
      alert('Failed');
    }
  };

  return (
    <div className="mt-4">
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Username</label>
          <input
            type="text"
            className="form-control"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        {!isLogin && (
          <>
            <div className="form-group">
              <label>Role</label>
              <select
                className="form-control"
                value={role}
                onChange={(e) => setRole(e.target.value)}
              >
                <option value="student">Student</option>
                <option value="lecturer">Lecturer</option>
                <option value="prl">PRL</option>
                <option value="pl">PL</option>
                <option value="admin">Admin</option>
              </select>
            </div>
            <div className="form-group">
              <label>Faculty Name</label>
              <input
                type="text"
                className="form-control"
                value={faculty_name}
                onChange={(e) => setFacultyName(e.target.value)}
              />
            </div>
          </>
        )}
        <button type="submit" className="btn btn-primary">
          {isLogin ? 'Login' : 'Register'}
        </button>
      </form>
      <button onClick={() => setIsLogin(!isLogin)} className="btn btn-link">
        {isLogin ? 'Need to register?' : 'Already registered? Login'}
      </button>
    </div>
  );
}

export default Auth;
