// src/Dashboard.js - Fixed & Extended Version
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard({ token, role }) {
  const [activeTab, setActiveTab] = useState('reports');

  // Common data states
  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [ratings, setRatings] = useState({});
  const [searchQuery, setSearchQuery] = useState('');

  // Courses state
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState('');
  const [lecturerAssignments, setLecturerAssignments] = useState({});

  // Classes state
  const [classes, setClasses] = useState([]);
  const [newClass, setNewClass] = useState('');

  // Lectures state
  const [lectures, setLectures] = useState([]);
  const [newLecture, setNewLecture] = useState('');

  // Monitoring data
  const [monitoringData, setMonitoringData] = useState([]);

  // Fetch data on tab change
  useEffect(() => {
    if (activeTab === 'reports') fetchReports();
    if (activeTab === 'courses') fetchCourses();
    if (activeTab === 'monitoring') fetchMonitoring();
    if (activeTab === 'classes') fetchClasses();
    if (activeTab === 'lectures') fetchLectures();
  }, [activeTab]);

  // API fetch functions
  const fetchReports = async (query='') => {
    try {
      const url = query ? `/api/reports/search?query=${encodeURIComponent(query)}` : '/api/reports/view';
      const res = await axios.get(`http://localhost:5000${url}`, { headers: { Authorization: token } });
      setReports(res.data);
    } catch (err) {
      alert('Failed to load reports');
    }
  };

  const fetchCourses = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/courses', { headers: { Authorization: token } });
      setCourses(res.data);
    } catch (err) {
      alert('Failed to load courses');
    }
  };

  const fetchMonitoring = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/monitoring', { headers: { Authorization: token } });
      setMonitoringData(res.data);
    } catch (err) {
      alert('Failed to load monitoring data');
    }
  };

  const fetchClasses = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/classes', { headers: { Authorization: token } });
      setClasses(res.data);
    } catch (err) {
      alert('Failed to load classes');
    }
  };

  const fetchLectures = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/lectures', { headers: { Authorization: token } });
      setLectures(res.data);
    } catch (err) {
      alert('Failed to load lectures');
    }
  };

  // Handlers for Courses
  const handleAddCourse = async () => {
    try {
      await axios.post('http://localhost:5000/api/courses', { name: newCourse }, { headers: { Authorization: token } });
      setNewCourse('');
      fetchCourses();
    } catch (err) {
      alert('Failed to add course');
    }
  };

  const handleAssignLecturer = async (courseId) => {
    const lecturerId = lecturerAssignments[courseId];
    try {
      await axios.put(`http://localhost:5000/api/courses/${courseId}/assign`, { lecturerId }, { headers: { Authorization: token } });
      alert('Lecturer assigned');
      fetchCourses();
    } catch (err) {
      alert('Failed to assign lecturer');
    }
  };

  // Handlers for Classes
  const handleAddClass = async () => {
    try {
      await axios.post('http://localhost:5000/api/classes', { name: newClass }, { headers: { Authorization: token } });
      setNewClass('');
      fetchClasses();
    } catch (err) {
      alert('Failed to add class');
    }
  };

  // Handlers for Lectures
  const handleAddLecture = async () => {
    try {
      await axios.post('http://localhost:5000/api/lectures', { title: newLecture }, { headers: { Authorization: token } });
      setNewLecture('');
      fetchLectures();
    } catch (err) {
      alert('Failed to add lecture');
    }
  };

  // Feedback & Rating handlers
  const handleFeedback = async (id) => {
    if (role !== 'prl') return;
    try {
      await axios.put(`http://localhost:5000/api/reports/feedback/${id}`, 
        { feedback: feedback[id] }, 
        { headers: { Authorization: token } }
      );
      alert('Feedback added');
      fetchReports();
    } catch (err) {
      alert('Failed to add feedback');
    }
  };

  const handleRate = async (id) => {
    if (!ratings[id] || ratings[id] < 1 || ratings[id] > 5) {
      alert('Please enter a rating between 1 and 5');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/rate', 
        { report_id: id, rating: parseInt(ratings[id]) }, 
        { headers: { Authorization: token } }
      );
      alert('Rated successfully');
      fetchReports();
    } catch (err) {
      alert('Failed to rate');
    }
  };

  const downloadExcel = async (id) => {
    try {
      const res = await axios.get(`http://localhost:5000/api/reports/download/${id}`, { headers: { Authorization: token }, responseType: 'blob' });
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report_${id}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      alert('Download failed');
    }
  };

  // Chart Data
  const attendanceData = {
    labels: reports.map(r => r.course_name || 'Unknown'),
    datasets: [
      {
        label: 'Actual Students',
        data: reports.map(r => r.actual_students || 0),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
      {
        label: 'Registered Students',
        data: reports.map(r => r.total_registered_students || 0),
        backgroundColor: 'rgba(153, 102, 255, 0.6)',
      },
    ],
  };

  const ratingData = {
    labels: reports.map(r => r.course_name || 'Unknown'),
    datasets: [
      {
        label: 'Ratings',
        data: reports.map(r => r.rating || 0),
        backgroundColor: 'rgba(255, 99, 132, 0.6)',
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: { 
      legend: { position: 'top' }, 
      title: { display: true, text: 'Attendance Overview' } 
    },
  };

  return (
    <div className="mt-4">
      <h2>Dashboard - {role.toUpperCase()}</h2>
      
      {/* Tabs Navigation */}
      <div className="mb-4">
        {['reports', 'courses', 'monitoring', 'classes', 'lectures'].map((tab) => (
          <button
            key={tab}
            className={`btn btn-${activeTab===tab?'primary':'secondary'} mr-2`}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {/* Reports Tab */}
      {activeTab === 'reports' && (
        <>
          {/* Search */}
          <div className="mb-3">
            <input
              type="text"
              placeholder="Search by course or week..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="form-control d-inline-block w-25 mr-2"
            />
            <button onClick={() => fetchReports(searchQuery)} className="btn btn-info mr-2">Search</button>
            <button onClick={() => { setSearchQuery(''); fetchReports(); }} className="btn btn-secondary">Clear</button>
          </div>

          {/* Charts */}
          {reports.length > 0 && (
            <>
              <div style={{ height: '400px', margin: '20px 0' }}>
                <Bar data={attendanceData} options={chartOptions} />
              </div>
              <div style={{ height: '400px', margin: '20px 0' }}>
                <Bar
                  data={ratingData}
                  options={{
                    ...chartOptions,
                    plugins: { ...chartOptions.plugins, title: { ...chartOptions.plugins.title, text: 'Rating Overview' } },
                  }}
                />
              </div>
            </>
          )}

          {/* Reports Table */}
          <table className="table table-striped">
            <thead>
              <tr>
                <th>ID</th>
                <th>Course</th>
                <th>Week</th>
                <th>Lecturer</th>
                <th>Feedback</th>
                <th>Rating</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {reports.length === 0 ? (
                <tr><td colSpan="7" className="text-center">No reports found</td></tr>
              ) : (
                reports.map((report) => (
                  <tr key={report.id}>
                    <td>{report.id}</td>
                    <td>{report.course_name}</td>
                    <td>{report.week_of_reporting}</td>
                    <td>{report.lecturer_name}</td>
                    <td>{report.feedback || 'None'}</td>
                    <td>{report.rating ? `${report.rating}/5` : 'Not rated'}</td>
                    <td>
                      <div className="d-flex flex-column">
                        {role==='prl' && (
                          <>
                            <input
                              type="text"
                              placeholder="Add feedback..."
                              className="mb-2"
                              onChange={(e) => setFeedback({ ...feedback, [report.id]: e.target.value })}
                            />
                            <button className="btn btn-success btn-sm mb-2" onClick={() => handleFeedback(report.id)}>Add Feedback</button>
                          </>
                        )}
                        <input
                          type="number"
                          min="1"
                          max="5"
                          placeholder="Rate 1-5"
                          className="mb-2"
                          onChange={(e) => setRatings({ ...ratings, [report.id]: e.target.value })}
                        />
                        <button className="btn btn-warning btn-sm mb-2" onClick={() => handleRate(report.id)}>Rate</button>
                        <button className="btn btn-primary btn-sm" onClick={() => downloadExcel(report.id)}>Excel</button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </>
      )}

      {/* Courses Tab */}
      {activeTab === 'courses' && (
        <div>
          <h4>Manage Courses</h4>
          <div className="mb-3">
            <input
              type="text"
              placeholder="New Course Name"
              value={newCourse}
              onChange={(e) => setNewCourse(e.target.value)}
              className="form-control d-inline-block w-50 mr-2"
            />
            <button className="btn btn-primary" onClick={handleAddCourse}>Add Course</button>
          </div>
          <table className="table table-bordered">
            <thead><tr><th>Name</th><th>Assign Lecturer</th></tr></thead>
            <tbody>
              {courses.map(c => (
                <tr key={c.id}>
                  <td>{c.name}</td>
                  <td>
                    <select
                      className="form-control d-inline-block w-50"
                      onChange={(e) => setLecturerAssignments({ ...lecturerAssignments, [c.id]: e.target.value })}
                      value={lecturerAssignments[c.id] || ''}
                    >
                      <option value="">Select Lecturer</option>
                      {/* Static options, replace with dynamic data if available */}
                      <option value="1">Lecturer 1</option>
                      <option value="2">Lecturer 2</option>
                    </select>
                    <button className="btn btn-success btn-sm ml-2" onClick={() => handleAssignLecturer(c.id)}>Assign</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Monitoring Tab */}
      {activeTab === 'monitoring' && (
        <div>
          <h4>Monitoring</h4>
          {monitoringData.length === 0 ? (
            <p>No monitoring data available.</p>
          ) : (
            <ul className="list-group">
              {monitoringData.map((item, index) => (
                <li key={index} className="list-group-item">{item.description}</li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* Classes Tab */}
      {activeTab === 'classes' && (
        <div>
          <h4>Manage Classes</h4>
          <div className="mb-3">
            <input
              type="text"
              placeholder="New Class Name"
              value={newClass}
              onChange={(e) => setNewClass(e.target.value)}
              className="form-control d-inline-block w-50 mr-2"
            />
            <button className="btn btn-primary" onClick={handleAddClass}>Add Class</button>
          </div>
          <ul className="list-group">
            {classes.map(c => (
              <li key={c.id} className="list-group-item">{c.name}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Lectures Tab */}
      {activeTab === 'lectures' && (
        <div>
          <h4>Manage Lectures</h4>
          <div className="mb-3">
            <input
              type="text"
              placeholder="Lecture Title"
              value={newLecture}
              onChange={(e) => setNewLecture(e.target.value)}
              className="form-control d-inline-block w-50 mr-2"
            />
            <button className="btn btn-primary" onClick={handleAddLecture}>Add Lecture</button>
          </div>
          <ul className="list-group">
            {lectures.map(l => (
              <li key={l.id} className="list-group-item">{l.title}</li>
            ))}
          </ul>
        </div>
      )}

    </div>
  );
}

export default Dashboard;
