// src/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function Dashboard({ token, role }) {
  const [activeTab, setActiveTab] = useState("reports");

  // Data states
  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState({});
  const [ratings, setRatings] = useState({});
  const [searchQuery, setSearchQuery] = useState("");

  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState("");
  const [lecturerAssignments, setLecturerAssignments] = useState({});

  const [classes, setClasses] = useState([]);
  const [newClass, setNewClass] = useState("");

  const [lectures, setLectures] = useState([]);
  const [newLecture, setNewLecture] = useState("");

  const [monitoringData, setMonitoringData] = useState([]);

  const [newReport, setNewReport] = useState({
    faculty_name: "",
    class_name: "",
    week_of_reporting: "",
    date_of_lecture: "",
    course_name: "",
    course_code: "",
    lecturer_name: "",
    actual_students: "",
    total_registered_students: "",
    venue: "",
    scheduled_time: "",
    topic_taught: "",
    learning_outcomes: "",
    recommendations: "",
  });

  // Fetch functions
  const fetchReports = async (query = "") => {
    try {
      const url = query
        ? `/api/reports/search?query=${encodeURIComponent(query)}`
        : "/api/reports/view";
      const res = await axios.get(`http://localhost:5000${url}`, {
        headers: { Authorization: token },
      });
      setReports(res.data || []);
    } catch {
      alert("Failed to load reports");
    }
  };

  const fetchCourses = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/courses", {
        headers: { Authorization: token },
      });
      setCourses(res.data || []);
    } catch {
      alert("Failed to load courses");
    }
  };

  const fetchClasses = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/classes", {
        headers: { Authorization: token },
      });
      setClasses(res.data || []);
    } catch {
      alert("Failed to load classes");
    }
  };

  const fetchLectures = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/lectures", {
        headers: { Authorization: token },
      });
      setLectures(res.data || []);
    } catch {
      alert("Failed to load lectures");
    }
  };

  const fetchMonitoring = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/monitoring", {
        headers: { Authorization: token },
      });
      setMonitoringData(res.data || []);
    } catch {
      alert("Failed to load monitoring data");
    }
  };

  // Handlers
  const handleAddCourse = async () => {
    if (!newCourse.trim()) return;
    try {
      await axios.post(
        "http://localhost:5000/api/courses",
        { name: newCourse.trim() },
        { headers: { Authorization: token } }
      );
      setNewCourse("");
      fetchCourses();
    } catch {
      alert("Failed to add course");
    }
  };

  const handleAssignLecturer = async (courseId) => {
    const lecturerId = lecturerAssignments[courseId];
    if (!lecturerId) return;
    try {
      await axios.put(
        `http://localhost:5000/api/courses/${courseId}/assign`,
        { lecturerId },
        { headers: { Authorization: token } }
      );
      alert("Lecturer assigned");
      fetchCourses();
    } catch {
      alert("Failed to assign lecturer");
    }
  };

  const handleAddClass = async () => {
    if (!newClass.trim()) return;
    try {
      await axios.post(
        "http://localhost:5000/api/classes",
        { name: newClass.trim() },
        { headers: { Authorization: token } }
      );
      setNewClass("");
      fetchClasses();
    } catch {
      alert("Failed to add class");
    }
  };

  const handleAddLecture = async () => {
    if (!newLecture.trim()) return;
    try {
      await axios.post(
        "http://localhost:5000/api/lectures",
        { title: newLecture.trim() },
        { headers: { Authorization: token } }
      );
      setNewLecture("");
      fetchLectures();
    } catch {
      alert("Failed to add lecture");
    }
  };

  const handleFeedback = async (id) => {
    if (role !== "prl") return;
    try {
      await axios.put(
        `http://localhost:5000/api/reports/feedback/${id}`,
        { feedback: feedback[id] || "" },
        { headers: { Authorization: token } }
      );
      alert("Feedback added");
      fetchReports();
    } catch {
      alert("Failed to add feedback");
    }
  };

  const handleRate = async (id) => {
    const rate = parseInt(ratings[id]);
    if (!rate || rate < 1 || rate > 5) {
      alert("Enter a rating 1-5");
      return;
    }
    try {
      await axios.post(
        "http://localhost:5000/api/rate",
        { report_id: id, rating: rate },
        { headers: { Authorization: token } }
      );
      alert("Rated successfully");
      fetchReports();
    } catch {
      alert("Failed to rate");
    }
  };

  const downloadExcel = async (id) => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/reports/download/${id}`,
        { headers: { Authorization: token }, responseType: "blob" }
      );
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", `report_${id}.xlsx`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch {
      alert("Download failed");
    }
  };

  const handleAddReport = async () => {
    try {
      await axios.post(
        "http://localhost:5000/api/reports",
        newReport,
        { headers: { Authorization: token } }
      );
      alert("Report added");
      setNewReport({
        faculty_name: "",
        class_name: "",
        week_of_reporting: "",
        date_of_lecture: "",
        course_name: "",
        course_code: "",
        lecturer_name: "",
        actual_students: "",
        total_registered_students: "",
        venue: "",
        scheduled_time: "",
        topic_taught: "",
        learning_outcomes: "",
        recommendations: "",
      });
      fetchReports();
    } catch {
      alert("Failed to add report");
    }
  };

  // Role-based tabs
  const roleTabs = {
    student: ["monitoring", "reports"],
    lecturer: ["classes", "reports", "monitoring"],
    prl: ["courses", "reports", "monitoring", "classes"],
    pl: ["courses", "reports", "monitoring", "classes", "lectures"],
  };

  useEffect(() => {
    if (activeTab === "reports") fetchReports();
    if (activeTab === "courses") fetchCourses();
    if (activeTab === "classes") fetchClasses();
    if (activeTab === "lectures") fetchLectures();
    if (activeTab === "monitoring") fetchMonitoring();
  }, [activeTab]);

  // Chart Data
  const attendanceData = {
    labels: reports.map((r) => r.course_name || "Unknown"),
    datasets: [
      {
        label: "Actual Students",
        data: reports.map((r) => Number(r.actual_students) || 0),
        backgroundColor: "rgba(75,192,192,0.6)",
      },
      {
        label: "Registered Students",
        data: reports.map((r) => Number(r.total_registered_students) || 0),
        backgroundColor: "rgba(153,102,255,0.6)",
      },
    ],
  };

  const ratingData = {
    labels: reports.map((r) => r.course_name || "Unknown"),
    datasets: [
      {
        label: "Ratings",
        data: reports.map((r) => Number(r.rating) || 0),
        backgroundColor: "rgba(255,99,132,0.6)",
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "Attendance Overview" },
    },
  };

  return (
    <div className="mt-4 container">
      <h2>Dashboard - {role.toUpperCase()}</h2>

      {/* Tabs */}
      <div className="mb-4">
        {roleTabs[role]?.map((tab) => (
          <button
            key={tab}
            className={`btn me-2 mb-2 btn-${activeTab === tab ? "primary" : "secondary"}`}
            onClick={() => setActiveTab(tab)}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {/* Render each tab */}
      {activeTab === "reports" && (
        <ReportsTab
          role={role}
          reports={reports}
          newReport={newReport}
          setNewReport={setNewReport}
          handleAddReport={handleAddReport}
          searchQuery={searchQuery}
          setSearchQuery={setSearchQuery}
          fetchReports={fetchReports}
          feedback={feedback}
          setFeedback={setFeedback}
          ratings={ratings}
          setRatings={setRatings}
          handleFeedback={handleFeedback}
          handleRate={handleRate}
          downloadExcel={downloadExcel}
        />
      )}

      {activeTab === "courses" && (
        <CoursesTab
          courses={courses}
          newCourse={newCourse}
          setNewCourse={setNewCourse}
          handleAddCourse={handleAddCourse}
          lecturerAssignments={lecturerAssignments}
          setLecturerAssignments={setLecturerAssignments}
          handleAssignLecturer={handleAssignLecturer}
        />
      )}

      {activeTab === "classes" && (
        <ClassesTab classes={classes} newClass={newClass} setNewClass={setNewClass} handleAddClass={handleAddClass} />
      )}

      {activeTab === "lectures" && role === "pl" && (
        <LecturesTab lectures={lectures} newLecture={newLecture} setNewLecture={setNewLecture} handleAddLecture={handleAddLecture} />
      )}

      {activeTab === "monitoring" && (
        <MonitoringTab monitoringData={monitoringData} />
      )}

      {/* Charts */}
      {reports.length > 0 && (
        <>
          <div style={{ height: "400px", margin: "20px 0" }}>
            <Bar data={attendanceData} options={chartOptions} />
          </div>
          <div style={{ height: "400px", margin: "20px 0" }}>
            <Bar
              data={ratingData}
              options={{
                ...chartOptions,
                plugins: {
                  ...chartOptions.plugins,
                  title: { ...chartOptions.plugins.title, text: "Rating Overview" },
                },
              }}
            />
          </div>
        </>
      )}
    </div>
  );
}

// --- Separate Tab Components for clarity ---
const ReportsTab = ({
  role,
  reports,
  newReport,
  setNewReport,
  handleAddReport,
  searchQuery,
  setSearchQuery,
  fetchReports,
  feedback,
  setFeedback,
  ratings,
  setRatings,
  handleFeedback,
  handleRate,
  downloadExcel,
}) => (
  <>
    {role === "lecturer" && (
      <div className="card p-3 mb-3">
        <h5>Data Entry - Lecturer Reporting Form</h5>
        <div className="row g-2">
          {Object.keys(newReport).map((key) =>
            key.includes("learning_outcomes") || key.includes("recommendations") ? (
              <textarea
                key={key}
                placeholder={key.replace(/_/g, " ")}
                value={newReport[key]}
                onChange={(e) => setNewReport({ ...newReport, [key]: e.target.value })}
                className="form-control mb-2"
              />
            ) : (
              <input
                key={key}
                type={key.includes("students") ? "number" : key.includes("date") ? "date" : "text"}
                placeholder={key.replace(/_/g, " ")}
                value={newReport[key]}
                onChange={(e) => setNewReport({ ...newReport, [key]: e.target.value })}
                className="form-control mb-2"
              />
            )
          )}
          <button className="btn btn-success" onClick={handleAddReport}>
            Submit Report
          </button>
        </div>
      </div>
    )}

    <div className="mb-3 d-flex align-items-center">
      <input
        type="text"
        placeholder="Search by course or week..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="form-control me-2"
      />
      <button className="btn btn-info me-2" onClick={() => fetchReports(searchQuery)}>Search</button>
      <button className="btn btn-secondary" onClick={() => { setSearchQuery(""); fetchReports(); }}>Clear</button>
    </div>

    <table className="table table-striped">
      <thead>
        <tr>
          <th>ID</th>
          <th>Course</th>
          <th>Week</th>
          <th>Lecturer</th>
          <th>Feedback</th>
          <th>Rating</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {reports.length === 0 ? (
          <tr><td colSpan="7" className="text-center">No reports found</td></tr>
        ) : (
          reports.map((report) => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.course_name}</td>
              <td>{report.week_of_reporting}</td>
              <td>{report.lecturer_name}</td>
              <td>{report.feedback || "None"}</td>
              <td>{report.rating ? `${report.rating}/5` : "Not rated"}</td>
              <td>
                <div className="d-flex flex-column">
                  {role === "prl" && (
                    <>
                      <input
                        type="text"
                        placeholder="Add feedback..."
                        className="mb-2"
                        onChange={(e) => setFeedback({ ...feedback, [report.id]: e.target.value })}
                      />
                      <button className="btn btn-success btn-sm mb-2" onClick={() => handleFeedback(report.id)}>Add Feedback</button>
                    </>
                  )}
                  <input
                    type="number"
                    placeholder="Rate 1-5"
                    className="mb-2"
                    onChange={(e) => setRatings({ ...ratings, [report.id]: e.target.value })}
                  />
                  <button className="btn btn-primary btn-sm mb-2" onClick={() => handleRate(report.id)}>Rate</button>
                  <button className="btn btn-warning btn-sm" onClick={() => downloadExcel(report.id)}>Download</button>
                </div>
              </td>
            </tr>
          ))
        )}
      </tbody>
    </table>
  </>
);

const CoursesTab = ({ courses, newCourse, setNewCourse, handleAddCourse, lecturerAssignments, setLecturerAssignments, handleAssignLecturer }) => (
  <div className="card p-3 mb-3">
    <h5>Courses Management</h5>
    <div className="d-flex mb-2">
      <input
        type="text"
        placeholder="New Course"
        value={newCourse}
        onChange={(e) => setNewCourse(e.target.value)}
        className="form-control me-2"
      />
      <button className="btn btn-success" onClick={handleAddCourse}>Add Course</button>
    </div>
    <table className="table table-bordered">
      <thead>
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>Assign Lecturer</th>
        </tr>
      </thead>
      <tbody>
        {courses.map((course) => (
          <tr key={course.id}>
            <td>{course.id}</td>
            <td>{course.name}</td>
            <td>
              <div className="d-flex">
                <input
                  type="text"
                  placeholder="Lecturer ID"
                  className="form-control me-2"
                  onChange={(e) => setLecturerAssignments({ ...lecturerAssignments, [course.id]: e.target.value })}
                />
                <button className="btn btn-primary" onClick={() => handleAssignLecturer(course.id)}>Assign</button>
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);

const ClassesTab = ({ classes, newClass, setNewClass, handleAddClass }) => (
  <div className="card p-3 mb-3">
    <h5>Classes Management</h5>
    <div className="d-flex mb-2">
      <input
        type="text"
        placeholder="New Class"
        value={newClass}
        onChange={(e) => setNewClass(e.target.value)}
        className="form-control me-2"
      />
      <button className="btn btn-success" onClick={handleAddClass}>Add Class</button>
    </div>
    <ul>
      {classes.map((c) => (<li key={c.id}>{c.id}: {c.name}</li>))}
    </ul>
  </div>
);

const LecturesTab = ({ lectures, newLecture, setNewLecture, handleAddLecture }) => (
  <div className="card p-3 mb-3">
    <h5>Lectures Management</h5>
    <div className="d-flex mb-2">
      <input
        type="text"
        placeholder="New Lecture"
        value={newLecture}
        onChange={(e) => setNewLecture(e.target.value)}
        className="form-control me-2"
      />
      <button className="btn btn-success" onClick={handleAddLecture}>Add Lecture</button>
    </div>
    <ul>
      {lectures.map((l) => (<li key={l.id}>{l.id}: {l.title}</li>))}
    </ul>
  </div>
);

const MonitoringTab = ({ monitoringData }) => (
  <div className="card p-3 mb-3">
    <h5>Monitoring</h5>
    {monitoringData.length === 0 ? (
      <p>No monitoring data available.</p>
    ) : (
      <ul>
        {monitoringData.map((m) => (<li key={m.id}>{m.metric}: {m.value}</li>))}
      </ul>
    )}
  </div>
);

export default Dashboard;
